import React, { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { PageBreadcrumb } from "../breadcrumb/Breadcrumb.comp";
import { AddTicketForm } from "../add-ticket-from/AddTicketForm.comp";

export const Addticketpage = () => {
  const [cmbvalue, setcmbValue] = useState("Selectioner");
  const handleSelect = (e) => {
    console.log(e);
    setcmbValue(e);
  };
  const initialFrmData = {
    subject: "",
    issueDate: "",
    details: "",
  };
  const [frmData, setFrmData] = useState(initialFrmData);
  const handleOnChange = (e) => {
    const { name, value } = e.target;
    setFrmData({
      ...initialFrmData,
      [name]: value,
    });
    console.log(name, value);
  };

  const handleOnSubmit = (e) => {
    e.preventDefault();
    console.log("Form submit request receiveed");
  };
  return (
    <Container>
      <hr />
      <Row>
        <Col>
          <PageBreadcrumb page="Nouveau Ticket" />
        </Col>
      </Row>
      <Row>
        <Col>
          <AddTicketForm
            handleOnChange={handleOnChange}
            handleOnSubmit={handleOnSubmit}
            handleSelect={handleSelect}
            cmbvalue={cmbvalue}
            frmDt={frmData}
          />
        </Col>
      </Row>
    </Container>
  );
};
