import React, { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { PageBreadcrumb } from "../breadcrumb/Breadcrumb.comp";
import { AddTicketForm } from "../add-ticket-from/AddTicketForm.comp";

export const Addticketpage = () => {
  const [subject, setSubject] = useState("");
  const [priority, setPriority] = useState("");
  const [IssueDate, setIssueDate] = useState("");
  const handleOnChange = (e) => {
    const { name, value } = e.target;

    switch (name) {
      case "subject":
        setSubject(value);
        break;
      case "priority":
        setPriority(value);
        break;
      case "IssueDate":
        setIssueDate(value);
        break;
      default:
        break;
    }
    console.log(name, value);
  };

  const handleOnSubmit = (e) => {
    e.preventDefault();
    console.log("Form submit request receiveed");
  };
  return (
    <Container>
      <hr />
      <Row>
        <Col>
          <PageBreadcrumb page="Nouveau Ticket" />
        </Col>
      </Row>
      <Row>
        <Col>
          <AddTicketForm
            handleOnChange={handleOnChange}
            handleOnSubmit={handleOnSubmit}
            subject={subject}
            priority={priority}
            IssueDate={IssueDate}
          />
        </Col>
      </Row>
    </Container>
  );
};
