import React, { useState } from "react";
import { Jumbotron, Form, Button, Row, Col } from "react-bootstrap";
import DropdownButton from "react-bootstrap/DropdownButton";
import Dropdown from "react-bootstrap/Dropdown";
import PropTypes from "prop-types";
export const AddTicketForm = (handleOnChange, handleOnSubmit, frmData) => {
  const [cmbvalue, setcmbValue] = useState("Selectioner");
  const handleSelect = (e) => {
    console.log(e);
    setcmbValue(e);
  };
  return (
    <Jumbotron style={{ background: "none" }}>
      <Form autoComplete="off" onSubmit={handleOnSubmit}>
        <Form.Group as={Row}>
          <Form.Label column sm={3}>
            Sujet
          </Form.Label>
          <Col sm={9}>
            <Form.Control
              type="subject"
              name="subject"
              onChange={handleOnChange}
              value={frmData.subject}
              placeholder="Entrez le sujet de votre reclamation"
              required
            />
          </Col>
        </Form.Group>
        <Form.Group>
          <Form.Label>Date de reclamation</Form.Label>
          <Form.Control
            type="date"
            name="IssueDate"
            onChange={handleOnChange}
            value={frmData.issueDate}
            placeholder="La date"
            required
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Les details</Form.Label>
          <Form.Control
            as="textarea"
            //type="date"
            name="details"
            value={frmData.details}
            onChange={handleOnChange}
            placeholder="Mot de passe"
            required
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Priorit√©</Form.Label>
        </Form.Group>
        <Button type="submit" variant="info">
          Envoyer
        </Button>
      </Form>
    </Jumbotron>
  );
};
AddTicketForm.prototypes = {
  handleOnSubmit: PropTypes.func.isRequired,
  handleOnChange: PropTypes.func.isRequired,
  frmDt: PropTypes.object.isRequired,
};
