import React, { useState } from "react";
import { Jumbotron, Form, Button, Row, Col } from "react-bootstrap";
import DropdownButton from "react-bootstrap/DropdownButton";
import Dropdown from "react-bootstrap/Dropdown";
import PropTypes from "prop-types";
import "../../components/new-ticket/Addticketpage";
export const AddTicketForm = (handleOnChange, handleOnSubmit, frmDt) => {
  const [cmbvalue, setcmbValue] = useState("Selectioner");
  const handleSelect = (e) => {
    console.log(e);
    setcmbValue(e);
  };
  return (
    <Jumbotron style={{ background: "none" }}>
      <Form autoComplete="off" onSubmit={handleOnSubmit}>
        <Form.Group as={Row}>
          <Form.Label>Sujet</Form.Label>
          <Col>
            <Form.Control
              name="subject"
              value={subject}
              onChange={handleOnChange}
              placeholder="Entrez le sujet de votre reclamation"
              required
            />
          </Col>
        </Form.Group>
        <Form.Group>
          <Form.Label>Date de reclamation</Form.Label>
          <Form.Control
            type="date"
            name="IssueDate"
            //value={frmDt.issueDate}
            onChange={handleOnChange}
            placeholder="La date"
            required
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Les details</Form.Label>
          <Form.Control
            as="textarea"
            name="details"
            //value={frmDt.details}
            onChange={handleOnChange}
            placeholder="Mot de passe"
            required
          />
        </Form.Group>

        <Form.Group>
          <Form.Label>Priorité</Form.Label>
          <DropdownButton
            alignRight
            title={cmbvalue}
            value={cmbvalue}
            id="dropdown-menu-align-right"
            onSelect={handleSelect}
            required
          >
            <Dropdown.Item eventKey="Elevée">Elevée</Dropdown.Item>
            <Dropdown.Item eventKey="Moyenne">Moyenne</Dropdown.Item>
            <Dropdown.Item eventKey="Basse">Basse</Dropdown.Item>
          </DropdownButton>
        </Form.Group>
        <Button type="submit" variant="info">
          Envoyer
        </Button>
      </Form>
    </Jumbotron>
  );
};
AddTicketForm.prototypes = {
  handleOnSubmit: PropTypes.func.isRequired,
  handleOnChange: PropTypes.func.isRequired,
  frmDt: PropTypes.object.isRequired,
};
